package org.interviewquestions;

public class AddElementsinArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int array[]= {1,2,3,4};
		int sum=0;
		for(int i=0;i<array.length;i++)
		{
			sum=sum+array[i];
			
		}
System.out.println(sum);
	}

}
